sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(date) {
		var dateChecked = new GlideDateTime(date);
		var now = new GlideDateTime();

		return dateChecked.before(now) ? true : false;
	},

	isToday: function(date) {
		var dateChecked = new GlideDateTime(date);
		var today = new GlideDateTime();

		return (dateChecked.compareTo(today) === 0) ? true : false;
	},
    type: 'NeedItUtils'
};
